// Assets/Behaviors/AI_Gen/DealerWave.js
// Generated by MCTwin AI Architect

// Ensure Namespace exists (handled by bundle order, but safe to check)
if (!window.MCTwinBehaviors) window.MCTwinBehaviors = {};

window.MCTwinBehaviors["AI_DealerWave"] = {
    // A standard wave interaction
    onInteract: function (node, args) {
        node.metadata.isWaving = true;
        node.metadata.waveTimer = 2.0;
        node.metadata.startY = node.position.y;
    },
    onTick: function (node, args, time) {
        if (!node.metadata.isWaving) return;

        node.metadata.waveTimer -= 0.016;
        if (node.metadata.waveTimer <= 0) {
            node.metadata.isWaving = false;
            node.position.y = node.metadata.startY;
            node.rotation.z = 0;
            return;
        }

        // Animation Logic
        node.position.y = node.metadata.startY + Math.abs(Math.sin(time * 10)) * 0.5;
        node.rotation.z = Math.sin(time * 20) * 0.2;
    }
};
